INSERT INTO EmpresaDonacionInfo( codigo_empresa, descripcion_vidriera_donacion, en_vidriera_donacion, orden_vidriera_donacion, url_pagina_donacion, url_vidriera_donacion, url_imagen_descripcion_vidriera_donacion) VALUES ('BFCO','Fundación Leer es una organización sin fines de lucro que trabaja hace 20 años con un objetivo claro: contribuir a la educación de los niños y jóvenes a través de la promoción de la lectura. La Fundación lleva adelante programas educativos en todos los rincones del país junto a docentes, directivos, bibliotecarios, referentes comunitarios y familias, llegando a los sectores de mayor vulnerabilidad social.',1,4,'','images/donacion/BFCO.png','');
INSERT INTO EmpresaDonacionInfo( codigo_empresa, descripcion_vidriera_donacion, en_vidriera_donacion, orden_vidriera_donacion, url_pagina_donacion, url_vidriera_donacion, url_imagen_descripcion_vidriera_donacion) VALUES ('CILS','CILSA ONG por la inclusión, trabaja para promover la inclusión plena de personas con discapacidad y niños, niñas y adolescentes en situación de vulnerabilidad social. En sus 50 años de trayectoria lleva entregados más de 41.000 elementos ortopédicos a personas con discapacidad y bajos recursos.',1,5,'','images/donacion/CILS.png','');
INSERT INTO EmpresaDonacionInfo( codigo_empresa, descripcion_vidriera_donacion, en_vidriera_donacion, orden_vidriera_donacion, url_pagina_donacion, url_vidriera_donacion, url_imagen_descripcion_vidriera_donacion) VALUES ('INFA','Somos una organización de la sociedad civil (O.S.C), sin fines lucrativos situada en Arroyito provincia de Córdoba, Argentina, nos dedicamos a promover la prevención de accidentes infantiles para ello relevamos establecimientos educativos, capacitamos a docentes y dictamos talleres para niños en las escuelas.',1,3,'','images/donacion/INFA.png','');
INSERT INTO EmpresaDonacionInfo( codigo_empresa, descripcion_vidriera_donacion, en_vidriera_donacion, orden_vidriera_donacion, url_pagina_donacion, url_vidriera_donacion, url_imagen_descripcion_vidriera_donacion) VALUES ('ONUD','Vivienda Digna es una organización social que se compromete desde 1979 por el desarrollo humano, a través del acceso a una vivienda digna y un hábitat adecuado.',1,6,'','images/donacion/ONUD.png','');
INSERT INTO EmpresaDonacionInfo( codigo_empresa, descripcion_vidriera_donacion, en_vidriera_donacion, orden_vidriera_donacion, url_pagina_donacion, url_vidriera_donacion, url_imagen_descripcion_vidriera_donacion) VALUES ('QTWW','En el Banco de Alimentos ayudamos a quienes sufren hambre. Gestionamos donaciones de alimentos y las entregamos a quienes más las necesitan. Funcionamos como un puente entre la industria alimenticia y los comedores e instituciones que reciben ayuda .',1,2,'','images/donacion/QTWW.png','');
INSERT INTO EmpresaDonacionInfo( codigo_empresa, descripcion_vidriera_donacion, en_vidriera_donacion, orden_vidriera_donacion, url_pagina_donacion, url_vidriera_donacion, url_imagen_descripcion_vidriera_donacion) VALUES ('TTUN','Cáritas Argentina procura generar y dar respuestas integrales a las problemáticas de la pobreza desde los valores de la dignidad, la justicia y la solidaridad. Desarrolla en 66 Cáritas diocesanas y gracias al trabajo de más de 32.000 voluntarios programas de educación, primera infancia, abordaje comunitario de las adicciones, trabajo, alimentación, atención en emergencias y vivienda.',1,7,'','images/donacion/TTUN.png','');
INSERT INTO EmpresaDonacionInfo( codigo_empresa, descripcion_vidriera_donacion, en_vidriera_donacion, orden_vidriera_donacion, url_pagina_donacion, url_vidriera_donacion, url_imagen_descripcion_vidriera_donacion) VALUES ('UNCF','UNICEF está presente en Argentina desde mayo de 1985, brindando cooperación técnica y asistencia financiera al Estado y a la sociedad argentina en diferentes áreas vinculadas al bienestar de la infancia y la adolescencia. UNICEF Argentina trabaja para promover la protección de los derechos de niños, niñas y adolescentes, ayudar a satisfacer sus necesidades más importantes y aumentar las oportunidades que se les ofrecen, a fin de que alcancen el pleno desarrollo de sus capacidades.',1,1,'','images/donacion/UNCF.png','');
